installer-gui-script.script:

function pm_install_check() {
    if(!(system.version.ProductVersion >= '11.0')) {
        my.result.title = 'Srz Clipboard Installation Failed';
        my.result.message = 'Srz Clipboard requires macOS 11.0 or later.\n\nCurrent version: ' + system.version.ProductVersion;
        my.result.type = 'Fatal';
        return false;
    }
    return true;
}
